import React, { useEffect, useState } from "react";
import CarsData from "../../api/cars.json";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import CarListItem from "../CarListItem/CarListItem";
import { ICar } from "../../interface/cars";
import { FOUR, FOUR_HUNDRED_SIXTY_FOUR, ONE, THIRTY, THOUSAND, THREE_THOUSAND, THREE_THOUSAND_TWENTY_FOUR, TWO, ZERO } from "../../helpers/constants";

const CarsList = () => {
  const [data, setData] = useState<ICar[]>([]); // store data
  const [showDots, setShowDots] = useState(false); // hide dots for large screen

  useEffect(() => {
    setData(CarsData);
  }, []);

  //responsive breakpoints for plugin
  const responsive = {
    desktop: {
      breakpoint: { max: THREE_THOUSAND, min: THREE_THOUSAND_TWENTY_FOUR },
      items: FOUR,
      slidesToSlide: ONE, // optional, default to 1.
    },
    tablet: {
      breakpoint: { max: THREE_THOUSAND_TWENTY_FOUR, min: FOUR_HUNDRED_SIXTY_FOUR },
      items: TWO,
      slidesToSlide: ONE, // optional, default to 1.
      partialVisibilityGutter: 30,
    },
    mobile: {
      breakpoint: { max: FOUR_HUNDRED_SIXTY_FOUR, min:ZERO },
      items: ONE,
      slidesToSlide: ONE, // optional, default to 1.
      partialVisibilityGutter: THIRTY,
    },
  };

  const layoutResize = () => {
    // on window resize show dots only for small screen
    if (window.innerWidth > THREE_THOUSAND_TWENTY_FOUR) setShowDots(false);
    else setShowDots(true);
  };
  useEffect(() => {
    layoutResize();
    window.addEventListener("resize", layoutResize);
    return () => {
      window.removeEventListener("resize", layoutResize);
    };
  }, []);


  return (
    <>
      {data && data.length > 0 && (
        <Carousel
          swipeable={true}
          draggable={true}
          showDots={showDots}
          responsive={responsive}
          ssr={true} // means to render carousel on server-side.
          infinite={false}
          keyBoardControl={true}
          customTransition="transform 300ms ease-in-out"
          transitionDuration={THOUSAND}
          containerClass="carousel-container"
          removeArrowOnDeviceType={["tablet", "mobile"]}
          dotListClass="custom-dot-list-style"
          itemClass="carousel-item-padding-40-px"
          partialVisible={true}
        >
          {data.map((data: ICar, index: number) => {
            return <CarListItem key={index} data={data} />;
          })}
        </Carousel>
      )}
    </>
  );
};
export default React.memo(CarsList);
